import React from 'react'

const Cards = (props)=> {
    return (
        <div className="container my-3" style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'ccenter',
            alignItems: 'center',
            flexDirection: 'row'
        }}>
        {/* <h1>Location : {props.loc}</h1>
            <h3>Deaths : {props.deaths}</h3> */}
        {/* <h1>Deaths : {props.deaths} </h1> */}
        <div class="card" style={{
            width: '100%'
        }}>
          <div class="card-body">
            <h5 class="card-title text-info">Total Cases</h5>
            <p class="card-text">
              {props.cases}
            </p>
          </div>
        </div>

        <div class="card" style={{
            width: '100%'
        }}>
          <div class="card-body">
            <h5 class="card-title text-warning">Active Cases</h5>
            <p class="card-text">
              {props.cases - props.discharged - props.deaths}
            </p>
          </div>
        </div>

      </div>
    );
}

export default Cards
