import React from 'react'

const Cards = (props)=> {
    return (
        <>
        {/* <h1>Location : {props.loc}</h1>
            <h3>Deaths : {props.deaths}</h3> */}
        {/* <h1>Deaths : {props.deaths} </h1> */}
        <div className="card mx-3" style={{
            width: '30%',
            flexDirection: 'row'
        }}>
          <div className="card-body">
            <h5 className="card-title text-info">Total Cases</h5>
            <p className="card-text">
              { String(props.cases) }
            </p>
          </div>
        </div>

        <div className="card mx-3" style={{
            width: '30%',
            flexDirection: 'row'
        }}>
          <div className="card-body">
            <h5 className="card-title text-warning">Active Cases</h5>
            <p className="card-text">
              { String(props.cases - props.discharged - props.deaths) }
            </p>
          </div>
        </div>

        <div className="card mx-3" style={{
            width: '30%',
            flexDirection: 'row'
        }}>
          <div className="card-body">
            <h5 className="card-title text-danger">Total Deaths</h5>
            <p className="card-text">
              { String(props.deaths) }
            </p>
          </div>
        </div>

        <h4>{props.check}</h4>

        </>

    );
}

export default Cards
